Create table state ( code Int NOT NULL AUTO_INCREMENT, name Char(50) NOT NULL, UNIQUE (name), Primary Key (code)) ENGINE = InnoDB;
Create table city ( code Int NOT NULL AUTO_INCREMENT, name Char(50) NOT NULL, state_code Int NOT NULL, Primary Key (code)) ENGINE = InnoDB;
Create table administrator ( username Char(15) NOT NULL, password Char(100) NOT NULL, name Char(30) NOT NULL, Primary Key (username)) ENGINE = InnoDB;
Create table state_database ( state_code Int NOT NULL, database_name Char(50) NOT NULL, database_user_username Char(15) NOT NULL, database_user_password Char(100) NOT NULL, server_name Char(100) NOT NULL, time_of_creation Datetime NOT NULL, UNIQUE (database_name), Primary Key (state_code)) ENGINE = InnoDB;
Create table operator ( username Char(15) NOT NULL, password Char(100) NOT NULL, employee_id Char(20) NOT NULL, name Char(30) NOT NULL, is_blocked Bool NOT NULL, UNIQUE (employee_id), Primary Key (username)) ENGINE = InnoDB;
Create table product ( code Int NOT NULL AUTO_INCREMENT, name Char(75) NOT NULL, category_code Int, UNIQUE (name), Primary Key (code)) ENGINE = InnoDB;
Create table product_description (product_code Int NOT NULL, description Varchar(500), Primary Key (product_code)) ENGINE = InnoDB;
Create table product_size ( code Int NOT NULL AUTO_INCREMENT, product_code Int NOT NULL, size Char(25) NOT NULL, price Int NOT NULL, Primary Key (code)) ENGINE = InnoDB;
Create table product_price ( product_code Int NOT NULL, price Int NOT NULL, Primary Key (product_code)) ENGINE = InnoDB;
Create table customizable_ingredient ( code Int NOT NULL AUTO_INCREMENT, name Char(75) NOT NULL, customizable_ingredient_type_code Int NOT NULL, UNIQUE (name), Primary Key (code)) ENGINE = InnoDB;
Create table product_customizable_ingredient_mapping ( product_code Int NOT NULL, customizable_ingredient_code Int NOT NULL, Primary Key (product_code,customizable_ingredient_code)) ENGINE = InnoDB;
Create table customizable_ingredient_size ( code Int NOT NULL AUTO_INCREMENT, customizable_ingredient_code Int NOT NULL, size Char(25) NOT NULL, price Int NOT NULL, Primary Key (code)) ENGINE = InnoDB;
Create table customizable_ingredient_price ( customizable_ingredient_code Int NOT NULL, price Int NOT NULL, Primary Key (customizable_ingredient_code)) ENGINE = InnoDB;
Create table product_default_customizable_ingredient_mapping ( product_code Int NOT NULL, customizable_ingredient_code Int NOT NULL, Primary Key (product_code,customizable_ingredient_code)) ENGINE = InnoDB;
Create table additional_attribute ( code Int NOT NULL AUTO_INCREMENT,name Char(50) NOT NULL, additional_attribute_type_code Int NOT NULL, Primary Key (code)) ENGINE = InnoDB;
Create table product_non_chargeable_additional_attribute_mapping ( product_code Int NOT NULL, additional_attribute_code Int NOT NULL, Primary Key (product_code,additional_attribute_code)) ENGINE = InnoDB;
Create table product_chargeable_additional_attribute_mapping ( product_code Int NOT NULL, additional_attribute_code Int NOT NULL, price Int NOT NULL, Primary Key (product_code,additional_attribute_code)) ENGINE = InnoDB;
Create table product_with_multiple_additional_attributes ( product_code Int NOT NULL, Primary Key (product_code)) ENGINE = InnoDB;
Create table addditional_attribute_detail ( additional_attribute_code Int NOT NULL, description Varchar(300) NOT NULL, Primary Key (additional_attribute_code)) ENGINE = InnoDB;
Create table customizable_ingredient_detail ( customizable_ingredient_code Int NOT NULL, description Varchar(300) NOT NULL, Primary Key (customizable_ingredient_code)) ENGINE = InnoDB;
Create table additional_attribute_type ( code Int NOT NULL AUTO_INCREMENT, name Char(50) NOT NULL, UNIQUE (name), Primary Key (code)) ENGINE = InnoDB;
Create table customizable_ingredient_type ( code Int NOT NULL AUTO_INCREMENT, name Char(50) NOT NULL, UNIQUE (name), Primary Key (code)) ENGINE = InnoDB;
Create table product_with_multiple_customizable_ingredient_type ( product_code Int NOT NULL, customizable_ingredient_type_code Int NOT NULL, Primary Key (product_code,customizable_ingredient_type_code)) ENGINE = InnoDB;
Create table vegetarian_products (product_code Int NOT NULL, Primary Key (product_code)) ENGINE = InnoDB;
Create table non_vegetarian_product ( product_code Int NOT NULL, Primary Key (product_code)) ENGINE = InnoDB;
Create table vegetarian_non_vegetarian_heading ( code Int NOT NULL AUTO_INCREMENT, vegetarian_heading Char(50), non_vegetarian_heading Char(50), Primary Key (code)) ENGINE = InnoDB;
Create table category ( code Int NOT NULL AUTO_INCREMENT, name Char(70) NOT NULL, UNIQUE (name), Primary Key (code)) ENGINE = InnoDB;
Create table category_hierarchy ( category_code Int NOT NULL, category_parent_code Int NOT NULL, Primary Key (category_code,category_parent_code)) ENGINE = InnoDB;
